import React, {Component} from 'react';
import './body.css'

class Body extends Component{
    // state = {
    //     color:'blue',
    // }
    // handleMouseOver(e){
    //     this.setState({
    //     color:'white'
    //     })
    // }
    state = {
        bgcolor:'transparent',
        color:'white'
    }
    handleMouseOver = (e) =>{
        this.setState({
            [e.target.id]:'white',
            color:'black'
        })
    }
    handleMouseOut = (e) =>{
        this.setState({
            bgcolor:'transparent',
            color:'white'
        })
    }
    handleMouseOver1 = (e) =>{
        this.setState({
            bgcolor:'white',
            color:'black'
        })
    }
    handleMouseOut1 = (e) =>{
        this.setState({
            bgcolor:'transparent',
            color:'white'
        })
    }
    render() {
        return(
            <div class="mainPage">
                <div class = "mainPageContent">
                    <div class = "mainPageName">
                        Food App
                    </div>
                    <div class = "mainPageText">
                        EXPERIENCE THE JOY OF FOOD
                    </div>
                </div>
                <div class = "menu">
                    <button id="1" onMouseOver={this.handleMouseOver} onMouseOut={this.handleMouseOut} style={{background:this.state.bgcolor,color:this.state.color}}>See Menu</button>
                    <button id="2" onMouseOver={this.handleMouseOver} onMouseOut={this.handleMouseOut} style={{background:this.state.bgcolor,color:this.state.color}}>Order Online</button>
                    <button id="3" onMouseOver={this.handleMouseOver} onMouseOut={this.handleMouseOut} style={{background:this.state.bgcolor,color:this.state.color}}>Reservations</button>
                </div>
            </div>

        );
    }
}

export default Body;
